<script>
    export default {
        data() {
            return {
                me: [],
                active: 'active'
            }
        },
        mounted() {
            this.get_me()
        },
        methods: {
            get_me(){
                axios.get('/api/me', {
                    headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
                })
                .then(res => {
                    this.me = res.data.content
                })
            }
        },
    }
</script>

